import 'package:collection/collection.dart';
import 'package:ngroup/settings/settings.dart';

extension Conv on String {
  String get convUseSetting => Settings.convertChinese.val ? conv : this;
  String get conv {
    var conv = ConvTable.conv;
    var output = StringBuffer();
    for (int idx = 0; idx < length; idx++) {
      var from = this[idx];
      var to = from;
      if (conv[from] != null) {
        for (var item in conv[from]!) {
          var end = idx + item.$1.length;
          if (end > length) continue;
          var sub = substring(idx, end);
          if (sub == item.$2) break;
          if (sub == item.$1) {
            to = item.$2;
            idx += item.$1.length - 1;
            break;
          }
        }
      }
      output.write(to);
    }
    return output.toString();
  }
}

