      var i = input.split(',');
      var o = output.split(',');
      for (var zip in IterableZip([i, o])) {
        var k = zip[0][0];
        if (!_conv.containsKey(k)) {
          _conv[k] = [(zip[0], zip[1])];
        } else {
          _conv[k]!
            ..add((zip[0], zip[1]))
            ..sort((a, b) => b.$1.length.compareTo(a.$1.length));
        }
      }
    }
